// page init
jQuery(function() {
	initVideoBg();
	initCustomForms();
	initSameHeight();
	initMobileNav();
	initStickyBlock();
	initAnchors();
	initLightbox();
	initFixedScrollBlock();
	initAccordion();
	initTabs();
	initFitVids();
});

// handle flexible video size
function initFitVids() {
	jQuery('.lightbox-hold').fitVids();
}

jQuery(window).on('load', function() {
	initScrollToSection();
});

function initScrollToSection() {
	var hash = window.location.hash;
	var headerHeight = jQuery('#header').innerHeight();
	var delay = 300;

	if (hash.length) {
		var section = jQuery(hash.replace('-section', ''));

		if (section && section.length) {
			var topPosition = section.offset().top - headerHeight;

			setTimeout(function() {
				SmoothScroll.scrollTo(topPosition, 600);
			}, delay);
		}
	}
}

// accordion menu init
function initAccordion() {
	jQuery('.accordion').slideAccordion({
		opener: '.opener',
		slider: '.slide',
		animSpeed: 300
	});
}

// content tabs init
function initTabs() {
	jQuery('.tabset').tabset({
		tabLinks: 'a'
	});
}

// fancybox modal popup init
function initLightbox() {
	jQuery('a.lightbox').each(function() {
		var link = jQuery(this);
		var modal = jQuery(link.attr('href'));
		var iframe = modal.find('iframe');

		if (iframe.length) {
			link.fancybox({
				scrolling: 'visible',
				maxWidth: iframe.attr('width'),
				maxHeight: iframe.attr('height'),
				fitToView : false,
				width : '90%',
				height : '90%',
				autoSize : false,
				helpers: {
					overlay: {
						css: {
							background: 'rgba(31, 14, 29, 0.65)'
						}
					}
				},
				afterLoad: function(current, previous) {
					// handle custom close button in inline modal
					if(current.href.indexOf('#') === 0) {
						jQuery(current.href).find('.close').off('click.fb').on('click.fb', function(e){
							e.preventDefault();
							jQuery.fancybox.close();
						});
					}
				}
			});
		} else {
			link.fancybox({
				scrolling: 'visible',
				helpers: {
					overlay: {
						css: {
							background: 'rgba(31, 14, 29, 0.65)'
						}
					}
				},
				afterLoad: function(current, previous) {
					// handle custom close button in inline modal
					if(current.href.indexOf('#') === 0) {
						jQuery(current.href).find('.close').off('click.fb').on('click.fb', function(e){
							e.preventDefault();
							jQuery.fancybox.close();
						});
					}
				}
			});
		}
	});
}

// video background init
function initVideoBg() {
	jQuery('.video-box').videoBG();
}

// initialize fixed blocks on scroll
function initFixedScrollBlock() {
	jQuery('.two-columns').fixedScrollBlock({
		slideBlock: '.sidebar',
		positionType: 'fixed',
		fixedOnlyIfFits: false,
		extraTop: 208
	});
}

// initialize custom form elements
function initCustomForms() {
	jcf.setOptions('Select', {
		wrapNative: false,
		maxVisibleItems: 15
	});
	jcf.replaceAll();
}

// align blocks height
function initSameHeight() {
	jQuery('.plan-two-columns').sameHeight({
		elements: '.col-hold',
		flexible: true,
		multiLine: true,
		biggestHeight: true
	});
}

// mobile menu init
function initMobileNav() {
	jQuery('#nav').mobileNav({
		hideOnClickOutside: true,
		menuActiveClass: 'nav-active',
		menuOpener: '.nav-opener',
		menuDrop: '.drop'
	});
}

// init sticky header
function initStickyBlock() {
	jQuery('#wrapper').StickyBlock({
		fixedClass : 'visible-btn',
		heightRatio: 300
	});
}


// initialize smooth anchor links
function initAnchors() {
	new SmoothScroll({
		anchorLinks: '.anchor-link',
		extraOffset: function() {
			var totalHeight = 0;
			jQuery('#header').each(function(){
				totalHeight += jQuery(this).outerHeight();
			});
			return totalHeight;
		},
		activeClasses: 'link'
	});

	new SmoothScroll({
		anchorLinks: '.anchor-link-i',
		extraOffset: function() {
			var totalHeight = 0;
			jQuery('#header').each(function(){
				totalHeight += jQuery(this).outerHeight();
			});
			return totalHeight;
		},
		activeClasses: 'link'
	});
}

